SET NICK      = #{nick},
    INTRODUCE = #{introduce}
WHERE ID = #{id}
  AND QUIT_YN = 'N'

UPDATE MEMBER
SET INTRODUCE = #{introduce}
WHERE ID = #{id}
  AND QUIT_YN = 'N'

