mapper.update:
UPDATE MEMBER
SET QUIT_YN = 'Y'
WHERE NO = #{no}

UPDATE MEMBER
SET NICK      = #{nick},
    INTRODUCE = #{introduce}
WHERE NO = #{no}
  AND QUIT_YN = 'N'

UPDATE MEMBER
SET INTRODUCE = #{introduce}
WHERE NO = #{no}
  AND QUIT_YN = 'N'

UPDATE MEMBER
SET pwd = #{pwd}
WHERE NO = #{no}
  AND QUIT_YN = 'N'
