mapper.update:
UPDATE MEMBER
SET QUIT_YN = 'Y'
WHERE NO = #{no}

UPDATE MEMBER
SET NICK = #{nick}
WHERE NO = #{no}
  AND QUIT_YN = 'N'

UPDATE MEMBER
SET INTRODUCE = #{introduce}
WHERE NO = #{no}
  AND QUIT_YN = 'N'

UPDATE MEMBER
SET pwd = #{pwd}
WHERE NO = #{no}
  AND QUIT_YN = 'N'

mapper.select:
SELECT NO, ID, PWD, NAME, NICK, INTRODUCE, ENROLL_DATE, QUIT_YN
FROM MEMBER
WHERE NICK = #{value}
